css:
/* -------------------------------------------------
   1. LATIN FONTS (English) – normal range
   ------------------------------------------------- */
@font-face {
    font-family: 'ERPNext Latin';
    src: local("Helvetica Neue"), local(Arial), local(Inter), local(sans-serif);
    unicode-range: U+0000-007F, U+0080-00FF, U+0100-017F, U+0180-024F, U+1E00-1EFF, U+2000-206F, U+2070-209F, U+20A0-20CF, U+2100-214F, U+2150-218F, U+2190-21FF, U+2200-22FF, U+2300-23FF, U+25A0-25FF, U+2600-26FF;
}

/* -------------------------------------------------
   2. URDU FONT – only Arabic/Urdu script
   ------------------------------------------------- */
@font-face {
    font-family: 'Jameel Noori Nastaleeq Urdu';
    src: url('/files/Jameel-Noori-Nastaleeq.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
    unicode-range: U+0600-06FF, U+0750-077F, U+FB50-FDFF, U+FE70-FEFF, U+10E60-10E7F;
}

/* -------------------------------------------------
   3. BASE FONT STACK – Latin first, Urdu last
   ------------------------------------------------- */
.print-format,
.print-format * {
    font-family: 'ERPNext Latin', 'Jameel Noori Nastaleeq Urdu', sans-serif !important;
    font-size: 14px !important;
    line-height: 1.5 !important;
    direction: auto !important;
    text-align: left !important;
    margin: 0 !important;
    padding: 3px 0 !important;
}

/* -------------------------------------------------
   4. OPTIONAL: Slightly larger Urdu for readability
   ------------------------------------------------- */
.print-format * {
    /* Urdu characters get a tiny boost */
    font-size: 14px !important;
}

/* Force better rendering */
* {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility !important;
}

/* -------------------------------------------------
   5. Keep ALL your existing styles below
   ------------------------------------------------- */
/* === CONTAINER === */
.print-format {
    max-width: 700px !important;
    margin: 0 auto !important;
    padding: 10px 30px !important;
    box-sizing: border-box;
}

/* === HEADER === */
.print-heading {
    font-size: 26px !important;
    font-weight: bold !important;
    margin: 0 0 4px 0 !important;
}
.print-heading + p {
    font-size: 15px !important;
    margin: 0 0 12px 0 !important;
}

/* === TABLES === */
table { width: 100% !important; border-collapse: collapse; table-layout: fixed; }
td { vertical-align: top !important; word-wrap: break-word; padding: 3px 0 !important; }

/* === LABELS (ends with :) === */
td:contains(":") {
    font-weight: 600 !important;
    font-size: 15px !important;
    white-space: nowrap !important;
    width: 150px !important;
    padding-right: 15px !important;
}

/* === PAID AMOUNT === */
td:contains("Paid Amount"),
td:contains("Rs"),
td:contains("₨") {
    font-size: 18px !important;
    font-weight: bold !important;
    color: #27ae60 !important;
    background: #f8fff9 !important;
    padding: 6px 12px !important;
    border-radius: 6px !important;
    text-align: right !important;
    display: block !important;
    margin: 6px 0 !important;
}

/* === 10. SIGNATURE SECTION — PROPERLY ALIGNED & CENTERED === */
table:last-of-type {
    margin-top: 40px !important;
    margin-left: auto !important;
    margin-right: auto !important;
    width: 100% !important;
    max-width: 650px !important;
    border-collapse: collapse;
}

table:last-of-type td {
    width: 33.33% !important;
    text-align: center !important;
    padding: 0 15px !important;
    font-size: 13px !important;
    vertical-align: top !important;
}

table:last-of-type b {
    display: block !important;
    font-size: 15px !important;
    font-weight: 600 !important;
    margin-bottom: 50px !important;
    color: #2c3e50 !important;
    text-align: center !important;
}

table:last-of-type .signature-line {
    display: block !important;
    width: 80% !important;
    margin: 0 auto 8px auto !important;
    border-top: 1px solid #000 !important;
}
table:last-of-type .signature-name {
    display: block !important;
    margin-top: 4px !important;
    font-size: 13px !important;
    color: #2c3e50 !important;
    text-align: center !important;
}

table:last-of-type td:first-child .signature-line {
    display: none !important;
}

table:last-of-type td:first-child .signature-name {
    margin-top: -12px !important;
    margin-bottom: 8px !important;
    font-size: 13px !important;
}

/* === PRINT SETTINGS === */
@page { size: A4; margin: 12mm; }
@media print { body { -webkit-print-color-adjust: exact; } }
